def kaydet(dersadi,icerik):
    dosyaadi = f"{dersadi}.txt"
    with open(dosyaadi, "w", encoding="utf-8") as f:
        f.write(icerik)
    st.success(f"{dersadi} notlarƒ± kaydedildi")
    st.download_button(label = f"{dersadi} notlarƒ±nƒ± indir",
                       data = icerik,
                       file_name = dosyaadi,
                       mime = "text/plain")


